query.query:
query {
  countries {
    code
    name
    capital
    continent {
      code
      name
    }
  }
}

query {
  countries(filter: { code: "US' OR '1'='1'; --" }) {
    code
    name
    capital
  }
}

query {
  countries(filter: { name: "<script>alert('xss')</script>" }) {
    code
    name
    native
  }
}

query {
  countries(filter: { name: "http://169.254.169.254/latest/meta-data/iam/security-credentials/" }) {
    code
    name
    emoji
  }
}

query {
  countries(filter: { code: "US; /bin/cat /etc/passwd" }) {
    code
    name
    phone
  }
}

query {
  countries(filter: { name: "../../../../../etc/passwd" }) {
    code
    name
    native
  }
}

query {
  countries(filter: { name: "<img src=x onerror=alert('injected')>" }) {
    code
    name
    emoji
  }
}

query {
  countries(filter: { name: "France" }) {
    code
    name
    capital
  }
}

query {
  s: countries(filter: { name: "France" }) {
    countryCode: code
    name
    capital
  }
}

query {
  countries {
    code
    name
  }
  __type(name: "Country") {
    name
    fields {
      name
      type {
        name
        kind
      }
    }
  }
}
