query Introspection_QUERY {
  language {
    queryType {
      name
    }
    mutationType {
      name
    }
    subscriptionType {
      name
    }
    types {
      ...FullType
    }
    directives {
      name
      locations
      args {
        name
        type {
          kind
        }
      }
    }
  }
}

fragment FullType on __Type {
  kind
  name
  description
  fields(includeDeprecated: true) {
    name
    description
    args {
      name
      type {
        kind
      }
    }
    type {
      ...TypeRef
    }
  }
  inputFields {
    name
    description
    type {
      kind
    }
  }
  interfaces {
    kind
    name
    ofType {
      kind
      name
    }
  }
  enumValues(includeDeprecated: true) {
    name
    description
    isDeprecated
    deprecationReason
  }
  possibleTypes {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
      }
    }
  }
}

fragment TypeRef on __Type {
  kind
  name
  ofType {
    kind
    name
    ofType {
      kind
      name
    }
  }
}

query FieldSuggestions_QUERY {
  language(code: "en") {
    __typename
    ... on Language {
      name
      native
      countries {
        name
        capital
      }
    }
  }
}
