query.query:
query {
  documentaryUnits(first: 10, after: "1' OR '1'='1") {
    items {
      id
      identifier
      type
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 5, after: "<script>alert('xss')</script>") {
    items {
      id
      descriptions {
        name
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 5, after: "http://169.254.169.254/latest/meta-data/") {
    items {
      id
      repository {
        id
        identifier
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 3, after: "; cat /etc/passwd || true") {
    items {
      id
      identifier
      descriptions {
        archivistNote
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 5, after: "../../../../etc/passwd") {
    items {
      id
      identifier
      repository {
        id
        description {
          name
        }
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 5, after: "<img src=x onerror='alert(1)'>") {
    items {
      id
      descriptions {
        name
        archivalHistory
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 1) {
    items {
      id
      repository {
        id
        identifier
        description {
          name
          history
        }
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  s: documentaryUnits(first: 1) {
    items {
      id
      repository {
        id
        identifier
        description {
          name
          history
        }
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 1) {
    items {
      id
    }
  }
  __schema {
    types {
      name
      kind
      fields {
        name
      }
    }
  }
}

query {
  documentaryUnits(first: 1) {
    items {
      id
    }
  }
  __type(name: "DocumentaryUnit") {
    name
    fields {
      name
      type {
        name
        kind
      }
    }
  }
}

query {
  documentaryUnits(first: 3, topLevel: true) {
    items {
      id
      type
      identifier
      itemCount
      description {
        name
      }
      repository {
        id
        type
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}
