query.query:
query AuthoritativeSet(input: {id: AuthoritativeSet}) {
  id
  name
  itemCount
}

query DocumentaryUnits {
  AuthoritativeSet {
    id
    identifier
  }
  pageInfo {
    hasNextPage
  }
}

query {
  d1: AuthoritativeSet {
    i1: items {
      iid: id
    }
  }
  d2: DocumentaryUnits {
    i2: items {
      ident: identifier
    }
  }
}

query {
  AuthoritativeSet {
    types {
      name
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}
