  }
}

query AnnotationDenyBypass {
  a: Annotation(id: "annotation_009") {
    id
    type
    body
    field
  }
}

query IntrospectionQuery {
  Annotation {
    types {
      name
      kind
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

