    id
    type
    identifier
    itemCount
    description {
      name
      archivistNote
    }
    repository {
      id
      type
      description {
        name
      }
    }
  }
}

query bypassQuery {
  docUnitAlias: DocumentaryUnit(id: "DU-123") {
    idAlias: id
    typeAlias: type
    identifierAlias: identifier
    itemCountAlias: itemCount
    descriptionAlias: description {
      nameAlias: name
      archivistNoteAlias: archivistNote
    }
    repoAlias: repository {
      idRepoAlias: id
      typeRepoAlias: type
      descriptionRepoAlias: description {
        nameRepoAlias: name
      }
    }
  }
}

query IntrospectionQuery {
  DocumentaryUnit {
    types {
      name
      kind
      fields {
        name
        type {
          name
          kind
          ofType {
            name
            kind
          }
        }
      }
    }
    queryType {
      name
    }
    mutationType {
      name
    }
    subscriptionType {
      name
    }
  }
}

