query {
  Repository(id: "repo-SSRF-test") {
    id
    links {
      description # If descriptions are parsed from external URLs
      type # If 'type' could be influenced by external resources
    }
    # No direct URL arguments found in the schema for Repository or its direct children.
    # We target string fields that might be used internally to fetch resources.
    # "description" or other string fields, if they allow URLs, could be vulnerable.
    description {
        history # Often contains external references
        mandates # Could reference external documents
        records # Could reference external resources
    }
  }
}

