query {
  concepts {
    items {
      id
      type
      identifier
      uri
      description {
        name
        languageCode
      }
    }
  }
}

query bypassQuery {
  allConcepts: concepts {
    items {
      conceptId: id
      conceptType: type
      descriptionDetails: description {
        descName: name
        descLang: languageCode
      }
    }
  }
}

query IntrospectionQuery {
  concepts {
    types {
      name
      kind
      description
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

query {
  concepts {
    items {
      id
      typ # Malformed field to trigger suggestion
      identifir # Malformed field to trigger suggestion
      uri
    }
  }
}
