    # If the 'id' is used in constructing a file path on the backend without validation,
    # this could lead to path traversal.
    id
    identifier
  }
}

query {
  DocumentaryUnit(id: "docunit-html") {
    id
    description {
      archivistNote # If rendered directly on a webpage without escaping
      name # If rendered directly on a webpage without escaping
      languageCode
    }
  }
}

query {
  DocumentaryUnit(id: "docunit-deny-non-aliased") {
    id
    type
    itemCount
    repository {
      id
      itemCount
    }
  }
}

query {
  myUnit: DocumentaryUnit(id: "docunit-deny-aliased") {
    documentaryUnitId: id
    documentaryUnitType: type
    unitItemCount: itemCount
    associatedRepository: repository {
      repositoryId: id
      repositoryItemCount: itemCount
    }
  }
}

query IntrospectionQuery {
  DocumentaryUnit {
    types {
      name
      kind
      description
      fields {
        name
        description
        type {
          name
          kind
          ofType {
            name
            kind
          }
        }
        args {
          name
          description
          type {
            name
            kind
          }
        }
      }
    }
  }
}

query {
  DocumentaryUnit(id: "docunit-suggestions") {
    idd # Typo for 'id'
    typpe # Typo for 'type'
    identifiere # Typo for 'identifier'
    itemCountt # Typo for 'itemCount'
    descriptionn # Typo for 'description'
  }
}
