query {
  AuthoritativeSet(filter: "id = 'ehri_cb' OR '1'='1") {
    items {
      id
      name
    }
  }
}

query s: authoritativeSets(orderBy: ["name", "items.name"]) {
  ... s
}
fragments {
  s on authoritativeSets {
    AuthoritativeSet {
      id
      name
    }
  }
}

