    ...Query_Deny_Bypass
  }

  fragment Query_Deny_Bypass on CvocConcept {
    id
    type
  }
}

query {
  concepts {
    types {
      ...IntrospectionType
    }
  }

  fragment IntrospectionType on __Type {
    name
    kind
    fields {
      name
      type {
        name
      }
    }
  }
}
