    }
  }
}

query IntrospectionQuery {
  AuthoritativeSet {
    types {
      name
      kind
      fields {
        name
        type {
          name
        }
      }
    }
  }
}

query FieldSuggestionsQuery {
  AuthoritativeSet(
    initialValue: "authent"
  ) {
    keyCode
    applicableFields
  }
}

