query.query:
query AuthoritativeSet:AuthoritativeSetQuery {
  AuthoritativeSet(id: "valid ID", nameAlias: "name") {
    nameAlias
    description
  }
}

query {
  AuthoritativeSet {
    queryType {
      name
      fields {
        name
        type {
          name
        }
      }
    }
  }
}

query {
  AuthoritativeSet(id: "valid ID") {
    __typename
    ... on AuthoritativeSet {
      id
      type
      identifier
      name
      description
      itemCount
      authorities {
        items
      }
      links
      annotations
      systemEvents
    }
  }
}
