mutation {
  createComment(input: {
    postId: "b3a3f8d4-2c1e-4f9d-9a6b-0a6e1c2f4d5e",
    content: "Looks great! Thanks for sharing.",
    authorEmail: "jane.doe@example.com"
  }) {
    id
    content
    createdAt
    author { id name }
  }
}

