query.query:
mutation {
  createPost(input: {
    title: "Hello World"
    content: "This is a simple test post created as a basic call."
    authorId: "user-1234"
    tags: ["testing","basic"]
  }) {
    post {
      id
      title
    }
  }
}

mutation {
