query.query:
mutation {
  createPost(input: {
    title: "Introducing GraphQL Security",
    content: "This is a safe test post created on 2025-10-01 to validate API behavior.",
    authorEmail: "alice@example.com",
    publishAt: "2025-10-01T12:00:00Z"
  }) {
    id
    title
    authorEmail
    createdAt
  }
}

mutation {
  createPost(input: {
    title: "Test'; DROP TABLE users; --",
    content: "Attempting SQL injection in title field to see DB handling.",
    authorEmail: "bob@example.com"
  }) {
    id
    title
    status
  }
}

mutation {
  createPost(input: {
    title: "XSS test post",
    content: "<script>alert('xss-from-content')</script><p>benign</p>",
    authorEmail: "charlie@example.com"
  }) {
    id
    title
    content
  }
}

mutation {
  createPost(input: {
    title: "SSRF image probe",
    content: "Cover image points at a metadata endpoint to test SSRF.",
    authorEmail: "dana@example.com",
    coverImageUrl: "http://169.254.169.254/latest/meta-data/iam/security-credentials/"
  }) {
    id
    coverImageUrl
    createdAt
  }
}

mutation {
  createPost(input: {
    title: "OS command probe",
    content: "Testing command injection: `; /bin/sh -c 'cat /etc/passwd' ;`",
    authorEmail: "eve@example.com"
  }) {
    id
    title
  }
}

mutation {
  createPost(input: {
    title: "Path traversal attempt",
    content: "Trying to set path traversal in path field.",
    authorEmail: "frank@example.com",
    path: "/var/www/../../../../etc/passwd"
  }) {
    id
    path
  }
}

mutation {
  createPost(input: {
    title: "HTML injection test",
    content: "<img src=x onerror=\"this.onerror=null;alert('html-injection')\">Extra text.",
    authorEmail: "grace@example.com"
  }) {
    id
    content
  }
}

mutation {
  createPost(input: {
    title: "Privilege escalation attempt",
    content: "Trying to create a post as another user by specifying authorId.",
    authorEmail: "hacker@example.com",
    authorId: "3fa85f64-5717-4562-b3fc-2c963f66afa6",
    visibility: "PUBLIC"
  }) {
    id
    authorId
    visibility
  }
}

mutation {
  s: createPost(input: {
    title: "Privilege escalation attempt (aliased)",
    content: "Aliased call to verify deny/ACL bypass with same payload.",
    authorEmail: "hacker@example.com",
    authorId: "3fa85f64-5717-4562-b3fc-2c963f66afa6",
    visibility: "PUBLIC"
  }) {
    id
    authorId
    visibility
  }
}
