mutation {
  createPost(input: {
    title: "Security testing post - basic call",
    content: "This is a simple createPost call used to verify baseline functionality.",
    authorEmail: "alice@example.com",
    url: "https://blog.example.com/posts/security-testing-basic",
    publishDate: "2025-10-01T09:30:00Z",
    tags: ["security","test"],
    clientId: "2f6b4c2d-8f3a-4b2d-9c1a-7a3e9f2d6b1a"
  }) {
    id
    title
    url
    createdAt
    status
  }
}

