# mutation combined with an introspection query in the same document to test if introspection is allowed
mutation UpdateThenIntrospect {
  updateComment(id: "10", input: { name: "Introspect Tester", email: "introspect@example.com", body: "Trigger + introspect" }) {
    id
    name
    email
  }
}

query Introspection {
  __schema {
    types {
      name
      fields {
        name
      }
    }
  }
}

# Use introspection to list Comment fields (useful for field-suggestion / auto-complete checks)
mutation FieldSuggestor {
  updateComment(id: "18", input: { name: "Field Suggestion Probe", email: "fields@example.com", body: "Requesting available fields for Comment" }) {
    id
  }
}

query SuggestCommentFields {
  __type(name: "Comment") {
    name
    fields {
      name
      type {
        name
      }
    }
  }
}
