query.query:
mutation {
  updatePost(id: "1", input: {title: "Safe update", body: "A simple update to verify mutation behavior."}) {
    id
    title
    body
    user {
      id
      name
      username
      email
    }
    comments {
      data {
        id
        name
        email
        body
      }
      meta {
        totalCount
      }
    }
  }
}

