query.query:
mutation {
  createComment(
    input: {
      name: "John Doe",
      email: "john.doe@example.com",
      body: "This is a legitimate comment."
    }
  ) {
    id
    name
    email
    body
  }
}

mutation {
  createComment(
    input: {
      name: "SQL Injector",
      email: "test@example.com' OR '1'='1 --",
      body: "Testing SQL injection in email field."
    }
  ) {
    id
    name
    email
    body
  }
}

mutation {
  createComment(
    input: {
      name: "XSS Attacker",
      email: "xss@example.com",
