    name: "John Doe",
    username: "johndoe",
    email: "john.doe@example.com",
    address: {
      street: "123 Main St",
      suite: "Apt 101",
      city: "Anytown",
      zipcode: "12345",
      geo: {
        lat: 34.0522,
        lng: -118.2437
