query {
  createUser {
    queryType { name }
    mutationType { name }
    subscriptionType { name }
    types {
     ...FullType
    }
    directives {
      name
      description
    }
  }
}

fragment FullType on __Type {
  kind
  name
  description
  fields {
    name
    description
    args {
      name
      description
      type {
       ...TypeRef
      }
    }
  }
  inputFields {
    name
    description
    type {
     ...TypeRef
    }
  }
  interfaces {
    name
    description
  }
  enumValues {
    name
    description
  }
  possibleTypes {
    name
    description
  }
}

fragment TypeRef on __Type {
  kind
  name
  ofType {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
          ofType {
            kind
            name
          }
        }
      }
    }
  }
}

