query.query:
mutation CreatePhoto {
  createPhoto(input: {
    title: "Amazing Photo",
    url: "https://example.com/photo.jpg",
    thumbnailUrl: "https://example.com/thumb.jpg",
    album: {
      id: "Album ID",
      title: "Awesome Album",
      user: {
        id: "User ID",
        name: "John Doe",
        username: "johndoe",
        email: "johndoe@example.com",
        address: {
          street: "123 Main St",
          suite: "Apt 101",
          city: "Anytown",
          zipcode: "12345",
          geo: {
            lat: 40.7128,
            lng: -74.0060
          }
        },
        phone: "123-456-7890",
        website: "https://johndoe.com",
        company: {
          name: "ABC Company",
          catchPhrase: "Best in class",
          bs: "Customer satisfaction is key"
        },
        posts: {
          data: [
            {
              id: "Post ID",
              title: "My Post",
              body: "This is my post.",
              user: {
                id: "User ID",
                ...
              },
              comments: {
                data: [
                  {
                    id: "Comment ID",
                    name: "Commenter",
                    email: "commenter@example.com",
                    body: "This is my comment.",
                    post: {
                      id: "Post ID",
                      ...
                    }
                  }
                ]
              }
            }
          ]
        },
        albums: {
          data: [
            {
              id: "Album ID",
              title: "My Album",
              user: {
                id: "User ID",
                ...
              },
              photos: {
                data: [
                  {
                    id: "Photo ID",
                    title: "My Photo",
                    ...
                  }
                ]
              }
            }
          ]
        },
        todos: {
          data: [
            {
              id: "Todo ID",
              title: "My Todo",
              completed: false,
              user: {
                id: "User ID",
                ...
              }
            }
          ]
        }
      }
    }
  }) {
    id
    title
    url
    thumbnailUrl
    album {
      id
      title
      user {
        id
        name
      }
    }
  }
}

