query.query:
query locations {
  locations(page: 1) {
    info {
      count
      pages
    }
    results {
      id
      name
      type
      dimension
    }
  }
}

query locations {
  locations(filter: { name: "1' UNION SELECT * FROM locations--" }) {
    results {
      id
      name
      dimension
    }
  }
}

query locations {
  locations(filter: { type: "<script>alert(document.cookie)</script>" }) {
    results {
      name
      type
    }
  }
}

query locations {
  locations(filter: { dimension: "http://169.254.169.254/latest/meta-data/" }) {
    results {
      id
      name
    }
  }
}

query locations {
  locations(filter: { name: "test; ping -c 5 127.0.0.1" }) {
    results {
      id
      created
    }
  }
}

query locations {
  locations(filter: { type: "../../../../etc/passwd" }) {
    results {
      name
      dimension
    }
  }
}

query locations {
  locations(filter: { dimension: "<img src=x onerror=alert(1)>" }) {
    results {
      id
      residents {
        name
      }
    }
  }
}

query locations {
  locations {
    info {
      count
    }
    results {
      name
    }
  }
}

query locations {
  loc1: locations(page: 1) {
    i: info {
      p: pages
    }
    r: results {
      n: name
    }
  }
  loc2: locations(page: 2) {
    i: info {
      p: pages
    }
    r: results {
      n: name
    }
  }
}

query {
  locations {
    queryType { name }
    types {
      name
      kind
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

query {
  locations(name: "Location") {
    name
    fields {
      name
