query.query:
mutation {
  createTransaction(input: {
    amount: 42.50,
    payerID: "9f1b2c3d-4e5f-4789-abcd-0123456789ab",
    walletID: "wallet-7f3a-001",
    currencyID: "b1a2c3d4-0001-4e5f-8888-abcdef123456"
  }) {
    id
    amount
    rate
    description
    payer {
      id
      firstName
    }
    wallet {
      id
      name
      currency {
        abbreviation
      }
    }
    timestamp
  }
}

