  updateTransaction(input: {
    transactionID: "3fa85f64-5717-4562-b3fc-2c963f66afa6",
    amount: 199.99,
    payer: "alice@example.com",
    description: "Monthly subscription payment"
  }) {
    transactionID
    amount
    payer
    description
  }
}

