mutation {
  updateTransaction(input: {
    transactionID: "fd4b3a12-3a6b-4d9c-9f8a-1234567890ab",
    amount: 49.95,
    payer: "11111111-2222-3333-4444-555555555555",
    description: "Payment for coffee on 2025-10-01"
  }) {
    id
    amount
    description
    payer {
      id
      firstName
    }
    wallet {
