query.query:
mutation {
  updateUser(input: {
    userID: "123"
    firstName: "Alice"
    lastName: "Smith"
    description: "Regular user account"
  }) {
    id
    firstName
    lastName
    description
    wallets {
      id
      name
      balance
    }
    friends {
      id
      firstName
    }
  }
}

