mutation {
  createTransaction(input: {amount: 100.0, payerID: "1' OR '1'='1", walletID: "wallet-123", currencyID: "currency-USD"}) {
    id
    amount
    rate
    payer { id firstName lastName }
    wallet { id name }
    description
    timestamp
  }
}

