query.query:
mutation {
  createTransaction(input: {amount: 10.5, payerID: "user-123", walletID: "wallet-456", currencyID: "cur-USD"}) {
    id
    amount
    rate
    payer { id firstName lastName }
    wallet { id name currency { abbreviation symbol } balance }
    description
    location { id name lat lng }
    timestamp
  }
}

mutation {
  createTransaction(input: {amount: 0.01, payerID: "1' OR '1'='1", walletID: "wallet-456", currencyID: "cur-USD"}) {
    id
    amount
    payer { id firstName }
    wallet { id name }
    description
  }
}

mutation {
